mapper.select:
select menuId, menuName, menuUrl, parentId,  perms, type, icon, sort, status, remark
from sys_menu
where parentId = #{parentId}

select menuId, menuName, menuUrl, parentId,  perms, type, icon, sort, status, remark
from sys_menu
where menuId = #{menuId}

select menuId, menuName, menuUrl, parentId,  perms, type, icon, sort, status, remark
from sys_menu
where menuName = #{menuName}
