mapper.select:
		SELECT
		a.id,	a.nsrsbh,	a.nsrmc,	a.productName,	a.type,	a.name,	a.phone,	date_format(a.visitDate, '%Y-%m-%d') as visitDate
		FROM bb_question_accepted a
		LEFT JOIN bb_user u on u.phone = a.phone

            1=1
            AND a.phone = #{phone}
            AND u.id = #{userId}
            AND a.name = #{name}
            AND a.nsrsbh = #{nsrsbh}


                and date_format(visitDate, '%Y') = #{date}



		order by visitDate desc

		select count(id) as total,(select count(id) from bb_question_accepted) as allCount,date_format(visitDate, '%Y') as date from bb_question_accepted
		LEFT JOIN bb_user u on u.phone = a.phone

            1=1
            AND phone = #{phone}
			AND u.id = #{userId}


                and date_format(visitDate, '%Y') = #{date}



        group by date_format(visitDate, '%Y')
