mapper.select:
SELECT a.id, parentId, questionId, a.userId,u.userPicturePath,u.nickname, (SELECT nickname FROM bb_user where id = answerUserId) answerNickname, answerUserId,a.ip, answer, a.status, a.createTime, a.lastUpdate, isAccept, (SELECT count(likeId) FROM bb_question_like where id = a.id and userId = #{userId}) isLike, (SELECT count(likeId) FROM bb_question_like where id = a.id) likeNum, (SELECT count(id) FROM bb_question_answer where parentId = a.id) answerNum FROM bb_question_answer a left join bb_user u on a.userId = u.id WHERE parentId=''
AND questionId = #{questionId}
AND isAccept = #{isAccept}

SELECT a.id, parentId, questionId, a.userId,u.userPicturePath,u.nickname, (SELECT nickname FROM bb_user where id = answerUserId) answerNickname, answerUserId,a.ip, answer, a.status, a.createTime, a.lastUpdate, isAccept, (SELECT count(likeId) FROM bb_question_like where id = a.id and userId = #{userId}) isLike, (SELECT count(likeId) FROM bb_question_like where id = a.id) likeNum FROM bb_question_answer a left join bb_user u on a.userId = u.id WHERE 1=1
AND parentId = #{parentId}
AND questionId = #{questionId}
