mapper.select:
SELECT a.id, parentId, questionId,commentNum,likeNum,trampleNum,reportNum, a.userId,u.userPicturePath,u.nickname, (SELECT nickname FROM bb_user where id = answerUserId) answerNickname, answerUserId,a.ip, answer, a.status, a.createTime, a.lastUpdate, isAccept, (SELECT count(likeId) FROM bb_question_like where id = a.id and userId = #{userId}) isLike FROM bb_question_answer a left join bb_user u on a.userId = u.id WHERE parentId=''
AND questionId = #{questionId}
AND isAccept = #{isAccept}
order by createTime DESC

SELECT a.id, parentId, questionId,likeNum,trampleNum,reportNum, a.userId,u.userPicturePath,u.nickname, (SELECT nickname FROM bb_user where id = answerUserId) answerNickname, answerUserId,a.ip, answer, a.status, a.createTime, a.lastUpdate, isAccept, (SELECT count(likeId) FROM bb_question_like where id = a.id and userId = #{userId}) isLike FROM bb_question_answer a left join bb_user u on a.userId = u.id WHERE 1=1
AND parentId = #{parentId}
AND questionId = #{questionId}
order by createTime DESC

SELECT a.id, parentId, questionId, a.userId,u.userPicturePath,u.nickname,title, (SELECT nickname FROM bb_user where id = answerUserId) answerNickname, answerUserId,a.ip, answer, a.status, a.createTime, a.lastUpdate, isAccept, (SELECT count(id) FROM abc12366_bangbang.bb_question_tip_off where sourceId = a.id and sourceType = 'answer' and createUser = a.userId) isTip, (SELECT count(likeId) FROM bb_question_like where id = a.id and userId = a.userId) isLike, (SELECT count(likeId) FROM bb_question_like where id = a.id) likeNum, (SELECT count(id) FROM bb_question_answer where parentId = a.id) answerNum FROM bb_question_answer a left join bb_question q on a.questionId = q.id left join bb_user u on a.userId = u.id WHERE parentId=''
AND (SELECT count(id) FROM abc12366_bangbang.bb_question_tip_off where sourceId = a.id and sourceType = 'answer' and createUser = a.userId) > 0
AND isAccept = #{isAccept}
AND a.userId = #{userId}
order by a.createTime desc

SELECT a.id, parentId, questionId, a.userId,u.userPicturePath,u.nickname,title, (SELECT nickname FROM bb_user where id = answerUserId) answerNickname, answerUserId,a.ip, answer, a.status, a.createTime, a.lastUpdate, isAccept, (SELECT count(likeId) FROM bb_question_like where id = a.id and userId = a.userId) isLike, (SELECT count(likeId) FROM bb_question_like where id = a.id) likeNum FROM bb_question_answer a left join bb_question q on a.questionId = q.id left join bb_user u on a.userId = u.id WHERE parentId != ''
AND a.userId = #{userId}
order by a.createTime desc
