mapper.select:
SELECT
memberId,	factionId,	userId,	u.username,	u.nickname,	u.userPicturePath,u.status,duty,memberGrade,	m.createTime,	m.lastUpdate
FROM bb_question_faction_member m LEFT JOIN bb_user u on m.userId = u.id
WHERE 1=1

    AND factionId = #{factionId}


    AND m.status = #{status}

SELECT memberId, factionId, userId, u.username, u.nickname, u.userPicturePath,u.status,duty,memberGrade, m.createTime, m.lastUpdate, (SELECT count(*) FROM bb_question_attention where attentionUserId = m.userId and userId = #{userId}) isAttention, (SELECT count(id) FROM bb_question_answer where userId = m.userId and factionId = m.factionId) answerNum, (SELECT count(id) FROM bb_question_comment where userId = m.userId and factionId = m.factionId) discussNum, (SELECT count(id) FROM bb_question_answer where userId = m.userId and isAccept = '1' and factionId = m.factionId) adoptNum FROM bb_question_faction_member m LEFT JOIN bb_user u on m.userId = u.id WHERE 1=1 and m.status = '2'
AND factionId = #{factionId}
order by discussNum+2*answerNum+7*adoptNum desc
