mapper.select:
		SELECT
		v.id,	v.nsrsbh,	v.nsrmc,	v.productName,	v.name,	v.phone,v.visitDate
		FROM bb_return_visit v
		LEFT JOIN bb_user u on u.phone = v.phone

			1=1
			AND phone = #{phone}
			AND u.id = #{userId}


                and date_format(visitDate, '%Y') = #{date}



		order by visitDate desc

		select count(v.*) as total,date_format(v.visitDate, '%Y') as date from bb_return_visit v
		LEFT JOIN bb_user u on u.phone = v.phone

			1=1
			AND phone = #{phone}
			AND u.id = #{userId}


                and date_format(visitDate, '%Y') = #{date}



		group by date_format(visitDate, '%Y')
