mapper.select:
SELECT id,userId,sessionId,browseDate,year,week,referer,month,day,hour,minute,pageTitle,pageUrl
,stayLong,feature,appName,ip,location,browseType,browseVersion,os,device,remark,createTime
,(select username from bb_user bu where bu.id=userId) username
from bb_system_record 


        userId=#{userId}


        and appName=#{appName}


        and location=#{location}


        and createTime >=#{startTime}


        and createTime <= #{endTime}


ORDER BY createTime DESC
