mapper.select:
SELECT count(contentId) cnt
FROM cms_content
WHERE channelId = #{channelId}

SELECT a.contentId contentId, a.userid userid, a.username username, b.title title, b.titleColor titleColor, a.channelId channelId, c.channelName channelName, a.topLevel topLevel, a.typeId typeId, b.author author, e.views views, b.releaseDate releaseDate, a.status STATUS, b.needRegenerate needRegenerate, b.staticLink, s.siteName, a.recommendLevel recommendLevel FROM cms_content a LEFT JOIN cms_content_ext b ON a.contentId = b.contentId LEFT JOIN cms_channel c ON a.channelId = c.channelId LEFT JOIN cms_site s ON a.siteId = s.siteId LEFT JOIN cms_content_count e ON a.contentId = e.contentId WHERE 1=1
AND b.title like CONCAT('%',#{title},'%')
AND a.username like CONCAT('%',#{username},'%')
AND a.typeId = #{typeId}
AND a.topLevel = #{topLevel}
AND a.status = #{status}
AND a.recommendLevel = #{recommendLevel}
AND a.channelId = #{channelId}
ORDER BY a.topLevel,a.recommendLevel,b.releaseDate DESC

SELECT a.contentId contentId, a.userid userid, a.username username, b.title title, a.contentType, b.titleImg, b.origin, b.description, b.titleColor titleColor, a.channelId channelId, c.channelName channelName, a.topLevel topLevel, a.typeId typeId, b.author author, e.views views, b.releaseDate releaseDate, a.status STATUS, b.needRegenerate needRegenerate, a.recommendLevel recommendLevel, b.staticLink, d.siteName, c.channelPath,d.sitePath,d.domain FROM cms_content a LEFT JOIN cms_contenttagid t ON t.contentId = a.contentId LEFT JOIN cms_content_ext b ON a.contentId = b.contentId LEFT JOIN cms_channel c ON a.channelId = c.channelId LEFT JOIN cms_site d ON a.siteId = d.siteId LEFT JOIN cms_content_count e ON a.contentId = e.contentId WHERE a.status = 2 AND b.needRegenerate = 1
AND t.tagId = #{tagId}
AND a.channelId = #{channelId}
AND a.siteId = #{siteId}
ORDER BY e.views DESC

SELECT
a.contentId contentId,
a.typeId typeId
FROM cms_content a LEFT JOIN cms_content_ext b ON a.contentId = b.contentId
WHERE 1=1

	AND a.typeId = #{typeId}


	AND a.channelId = #{channelId}


	AND a.status = #{status}


	AND b.tplContent = #{tplContent}


	AND b.needRegenerate = #{needRegenerate}


	AND UNIX_TIMESTAMP(b.releaseDate) >= #{startTime}


	AND UNIX_TIMESTAMP(b.releaseDate) <= #{endTime}

ORDER BY a.topLevel,a.recommendLevel,b.releaseDate DESC

SELECT a.contentId contentId, a.userid userid, a.username username, b.title title, a.contentType, b.titleImg, b.origin, b.description, b.titleColor titleColor, a.channelId channelId, c.channelName channelName, a.topLevel topLevel, a.typeId typeId, b.author author, e.views views, b.releaseDate releaseDate, a.status STATUS, b.needRegenerate needRegenerate, a.recommendLevel recommendLevel, b.staticLink, d.siteName, c.channelPath,d.sitePath,d.domain FROM cms_content a LEFT JOIN cms_contenttagid t ON t.contentId = a.contentId LEFT JOIN cms_content_ext b ON a.contentId = b.contentId LEFT JOIN cms_channel c ON a.channelId = c.channelId LEFT JOIN cms_site d ON a.siteId = d.siteId LEFT JOIN cms_content_count e ON a.contentId = e.contentId WHERE a.status = 2 AND b.needRegenerate = 1
AND t.tagId = #{tagId}
AND a.channelId = #{channelId}
AND a.siteId = #{siteId}
AND UNIX_TIMESTAMP(b.releaseDate) >= #{startTime}
AND UNIX_TIMESTAMP(b.releaseDate) <= #{endTime}
ORDER BY b.releaseDate DESC

SELECT a.tagId,COUNT(a.tagId) priority FROM cms_contenttagid a LEFT JOIN cms_content b ON a.contentId = b.contentId WHERE 1=1
AND b.siteId = #{siteId}
AND b.channelId = #{channelId}
GROUP BY a.tagId

		SELECT
		COUNT(a.contentId) cnt
		FROM cms_content a LEFT JOIN cms_content_ext b ON a.contentId = b.contentId
		LEFT JOIN cms_channel c ON a.channelId = c.channelId
		LEFT JOIN cms_site d ON a.siteId = d.siteId
		LEFT JOIN cms_content_count e ON a.contentId = e.contentId
        LEFT JOIN cms_content_topic t ON a.contentId = t.contentId
		WHERE a.status = 2 and b.needRegenerate = 0

			AND a.typeId = #{typeId}


			AND c.channelName = #{channelName}


			AND a.channelId = #{channelId}


			AND b.tplContent = #{tplContent}


            AND t.topicId = #{topicId}


			AND UNIX_TIMESTAMP(b.releaseDate) >= #{startTime}


			AND UNIX_TIMESTAMP(b.releaseDate) <= #{endTime}

		ORDER BY a.topLevel,a.recommendLevel,b.releaseDate DESC

SELECT a.contentId contentId, a.userid userid, a.username username, b.title title, a.contentType, b.titleImg, b.origin, b.description, b.titleColor titleColor, a.channelId channelId, c.channelName channelName, a.topLevel topLevel, a.typeId typeId, b.author author, e.views views, b.releaseDate releaseDate, a.status STATUS, b.needRegenerate needRegenerate, a.recommendLevel recommendLevel, b.staticLink, d.siteName, c.channelPath,d.sitePath,d.domain FROM cms_content a LEFT JOIN cms_content_ext b ON a.contentId = b.contentId LEFT JOIN cms_channel c ON a.channelId = c.channelId LEFT JOIN cms_site d ON a.siteId = d.siteId LEFT JOIN cms_content_count e ON a.contentId = e.contentId LEFT JOIN cms_content_topic t ON a.contentId = t.contentId WHERE a.status = 2
AND a.typeId = #{typeId}
AND c.channelName = #{channelName}
AND a.channelId = #{channelId}
AND b.tplContent = #{tplContent}
AND b.needRegenerate = #{needRegenerate}
AND t.topicId = #{topicId}
AND UNIX_TIMESTAMP(b.releaseDate) >= #{startTime}
AND UNIX_TIMESTAMP(b.releaseDate) <= #{endTime}
ORDER BY a.topLevel,a.recommendLevel,b.releaseDate DESC

SELECT a.contentId contentId, a.userid userid, a.username username, b.title title, a.contentType, b.titleImg, b.origin, b.description, b.titleColor titleColor, a.channelId channelId, a.topLevel topLevel, a.typeId typeId, b.author author, e.views views, b.releaseDate releaseDate, a.status STATUS, b.needRegenerate needRegenerate, a.recommendLevel recommendLevel, b.staticLink, d.siteName, c.channelPath,d.sitePath,d.domain FROM cms_content a LEFT JOIN cms_content_ext b ON a.contentId = b.contentId LEFT JOIN cms_content_topic c ON a.contentId = c.topicId LEFT JOIN cms_site d ON a.siteId = d.siteId LEFT JOIN cms_content_count e ON a.contentId = e.contentId WHERE 1=1
AND a.typeId = #{typeId}
AND a.status = #{status}
AND c.topicId = #{topicId}
AND b.tplContent = #{tplContent}
AND b.needRegenerate = #{needRegenerate}
AND UNIX_TIMESTAMP(b.releaseDate) >= #{startTime}
AND UNIX_TIMESTAMP(b.releaseDate) <= #{endTime}
ORDER BY a.topLevel,a.recommendLevel,b.releaseDate DESC

SELECT a.contentId,b.title,c.channelPath,d.sitePath,'down' upordown FROM cms_content a LEFT JOIN cms_content_ext b ON a.contentId = b.contentId
	LEFT JOIN cms_channel c ON a.channelId = c.channelId LEFT JOIN cms_site d ON a.siteId = d.siteId
WHERE UNIX_TIMESTAMP(b.releaseDate) > #{releaseDate} AND a.channelId = #{channelId} ORDER BY UNIX_TIMESTAMP(b.releaseDate) ASC LIMIT 1;

SELECT a.contentId,b.title,c.channelPath,d.sitePath,d.domain domain,'up' upordown FROM cms_content a LEFT JOIN cms_content_ext b ON a.contentId = b.contentId
LEFT JOIN cms_channel c ON a.channelId = c.channelId LEFT JOIN cms_site d ON a.siteId = d.siteId
WHERE UNIX_TIMESTAMP(b.releaseDate) < #{releaseDate} AND a.channelId = #{channelId} ORDER BY UNIX_TIMESTAMP(b.releaseDate) DESC LIMIT 1;
