mapper.select:
SELECT
FROM cms_event_apply WHERE 1=1
AND (name like CONCAT('%',#{name},'%') or tel like CONCAT('%',#{name},'%') or email like CONCAT('%',#{name},'%') )
AND status = #{status}
AND eventId = #{eventId}

SELECT DATE_FORMAT(browsetime, '%Y%m%d') llsj,COUNT(recordId) cnt FROM cms_event_record
WHERE 1=1

    AND source = #{source}


    AND eventId = #{eventId}


    AND UNIX_TIMESTAMP(browsetime) >= #{startTime}


    AND UNIX_TIMESTAMP(browsetime) <= #{endTime}

GROUP BY DAY(browsetime)
