mapper.select:
select id, username, password, phone, enabled, lastPasswordResetDate, createDate, modifyDate,
authority as roles
from user a
left join authority b on a.id = b.userId
where username = #{username}
or phone = #{phone}

mapper.update:
update user
username = #{username}, password = #{password}, phone = #{phone}, enabled = #{enabled}, lastPasswordResetDate = #{lastPasswordResetDate}, modifyDate = #{modifyDate}
where id=#{id}
