mapper.select:
select id, uri, userAgent, userId, appId, ip, inTime, outTime, status, version,method
from gw_api_log_${startTime}


        appId = #{appId}

select id, uri, userAgent, userId, appId, ip, inTime, outTime, status, version,method
from gw_api_log_${yyyyMMdd}


        uri = #{uri}

select count(*)
from gw_api_log_${yyyyMMdd}



    and inTime >= #{startTime}




    and inTime <= #{endTime}




        and uri = #{uri}


        and appId = #{appId}


        and ip = #{ip}


        and method = #{method}
