mapper.select:
SELECT id, name, password, accessToken, lastResetTokenTime, startTime, endTime, remark, status,
createTime, lastUpdate
FROM gw_app


        id = #{id}


        AND name = #{name}


        AND accessToken = #{accessToken}


        AND status = #{status}

SELECT id, name, startTime, endTime, status, createTime, lastUpdate
FROM gw_app
WHERE id=#{id}
