mapper.select:
select id, appId, apiId, timesPerMinute, timesPerHour, timesPerDay, status, createTime, lastUpdate
from gw_app_setting

    appId = #{appId}
    AND apiId = #{apiId}
    AND id = #{apiId}

SELECT  a.id, a.appId, a.apiId, a.timesPerMinute, a.timesPerHour, a.timesPerDay,
b.name, b.uri, b.method, b.version, b.authentication,
a.status, a.createTime, a.lastUpdate
FROM gw_app_setting a LEFT JOIN gw_api b ON a.apiId = b.id

    b.name = #{name}
    AND b.uri = #{uri}
    AND b.method = #{method}
    AND b.version = #{version}
    AND a.appId = #{appId}
    AND a.status = #{status}
    AND b.status = '1'

SELECT  a.id, a.appId, a.apiId, a.timesPerMinute, a.timesPerHour, a.timesPerDay,
b.name, b.uri, b.method, b.version, b.authentication,
a.status, a.createTime, a.lastUpdate
FROM gw_app_setting a LEFT JOIN gw_api b ON a.apiId = b.id

    b.name = #{name}
    AND b.uri = #{uri}
    AND b.method = #{method}
    AND b.version = #{version}
    AND a.appId = #{appId}
    AND a.status = #{status}
    AND b.status = '1'
