mapper.select:
SELECT

FROM msg_business_message
WHERE userId = #{userId}
AND type = #{type}
AND busiType = #{busiType}
AND status = #{status}
AND createTime > date_sub(now(), interval '181 0:0:0' day_second)
ORDER BY createTime DESC

SELECT
mbm.id, mbm.userId, mbm.businessId, mbm.content, mbm.status, mbm.type, mbm.createTime,
mbm.lastUpdate, mbm.url,mbm.busiType,muu.username,muu.userPicturePath
FROM msg_business_message mbm join msg_uc_user muu on mbm.userId=muu.id
WHERE 1=1
and muu.username = #{username}
AND mbm.type = #{type}
AND mbm.busiType = #{busiType}
AND mbm.status = #{status}
and   mbm.createTime >= #{startDate}
and   mbm.createTime < #{endDate}
ORDER BY mbm.createTime DESC

SELECT count(*)
FROM msg_business_message
WHERE userId = #{userId}
AND type = #{type}
AND busiType = #{busiType}
AND status = '1'
AND createTime > date_sub(now(), interval '181 0:0:0' day_second)
