mapper.select:
select id, phone, code, expireDate, type
from msg_phone_code
where id=#{id}

select id, phone, code, expireDate, type
from msg_phone_code
where type=#{type}
and phone=#{phone}
and code=#{code}

select id, phone, code, expireDate, type
from msg_phone_code
where type=#{type}
and phone=#{phone}
order by expireDate DESC
