mapper.select:
SELECT
mu.id, mu.fromUserId, mu.toUserId, mu.content, mu.status, mu.type, mu.createTime, mu.lastUpdate,
u1.username fromuser,u1.userPicturePath fromUserPic,u2.username touser,u2.userPicturePath toUserPic
FROM msg_user_message mu join msg_uc_user u1 on mu.fromUserId=u1.id join msg_uc_user u2 on mu.toUserId=u2.id
WHERE 1=1
AND u1.username = #{fromuser}
AND u2.username = #{touser}
AND mu.status = #{status}
and   mu.createTime >= #{startDate}
and   mu.createTime < #{endDate}
ORDER BY mu.createTime DESC

SELECT count(*)
FROM msg_user_message
WHERE toUserId = #{userId}
AND type = #{type}
AND status = '1'
AND createTime > date_sub(now(), interval '181 0:0:0' day_second)
