mapper.update:
update uc_user_task_todo
finishedCount=#{finishedCount}, status=#{status}, lastUpdate=#{lastUpdate},
where id=#{id}

mapper.select:
SELECT

FROM uc_user_task_todo
WHERE userId = #{userId}
and sysTaskId = #{sysTaskId}
and startTime = #{startTime}

SELECT

FROM uc_user_task_todo
WHERE userId = #{userId}
and sysTaskId = #{sysTaskId}
ORDER BY lastupdate DESC 
LIMIT 1

SELECT

FROM uc_user_task_todo
WHERE userId = #{userId}
and sysTaskId = #{sysTaskId}
and type = '1'
and dateType = 'O'

SELECT

FROM uc_user_task_todo
WHERE userId = #{userId}
and sysTaskId = #{sysTaskId}
and createTime >= (SELECT CURDATE())
and createTime <(SELECT DATE_SUB(CURDATE(),INTERVAL -1 DAY))
