SELECT

FROM uc_check
WHERE userId = #{userId}
and checkDate = #{checkDate}

SELECT

FROM uc_check
WHERE userId = #{userId}
and checkDate >= #{startDate}
and checkDate < #{endDate}

SELECT

FROM uc_check
WHERE userId = #{userId}
and checkDate = #{checkDate}
