mapper.select:
SELECT id, couponName, couponMode, couponType, amountType,
validType, validStartTime, validEndTime, validDays, status,
createTime, lastUpdate
FROM uc_coupon

    couponMode = #{couponMode}
    AND couponType = #{couponType}

        AND status = #{status}
        AND status != '0'


order by lastUpdate DESC

SELECT

FROM uc_coupon_activity

    AND activityId = #{activityId}
    AND status = #{status}

order by lastUpdate DESC

SELECT id, userId, activityId, couponId, couponName, couponMode, couponType, param1, param2, amountType, validStartTime, validEndTime, description, status, createTime, lastUpdate, orderNo, categoryIds FROM uc_coupon_user
userId = #{userId} AND couponId = #{couponId}
ORDER BY couponType DESC

SELECT cu.id,cu.couponId,cu.couponName, cu.userId, u.username, cu.couponMode, cu.couponType,
cu.param1, cu.param2, cu.amountType, cu.status, cu.createTime,
cu.lastUpdate, cu.orderNo, cu.validStartTime, cu.validEndTime,cu.categoryIds
FROM uc_coupon_user cu LEFT JOIN uc_user u
on cu.userId = u.id

    cu.userId = #{userId}
    AND cu.activityId = #{activityId}
    AND cu.couponId = #{couponId}
    AND cu.validEndTime < #{validEndTime}
    AND cu.orderNo = #{orderNo}
    AND cu.categoryIds LIKE CONCAT('%',#{categoryIds},'%')



            AND cu.status = #{status}


            AND cu.status != '4'



order by cu.lastUpdate DESC

SELECT count(userId) FROM uc_coupon_user

    userId = #{userId}
    AND activityId = #{activityId}
    AND status = #{status}

SELECT

FROM uc_coupon_user

    userId = #{userId}
    AND orderNo = #{orderNo}
