mapper.select:
select t2.*from (
SELECT id,activityId,lotteryId,createTime,lotteryName,isluck,state,userId,upoint,ip,remake,addressId
,(select c.val1 from uc_lottery_activityprize c where c.lotteryId=z.lotteryId) as lotteryLevel
,(select d.send from uc_lottery d where d.id=z.lotteryId) as lotterySend
,(select a.username from uc_user a where a.id=z.userId) as userName
,(select b.name from uc_lottery_activity b where b.id=z.activityId) as  activityName
from uc_lottery_log z) t2


        isluck=#{isluck}


        and  userId=#{userId}


        and userName=#{userName}


        and activityName=#{activityName}


        and createTime >=#{startTime}


        and createTime <= #{endTime}




ORDER BY createTime DESC
