mapper.select:
select e.id, e.orderNo, e.name, q.imageUrl, e.username, e.num, e.orderTime, e.type, e.reason, e.userRemark, e.expressNo, e.expressComp, e.toExpressNo, e.adminRemark, e.status, e.createTime, e.lastUpdate
from (select c.*, d.productId, d.num, d.name from (select b.username, b.createTime as orderTime, a.* from uc_order_exchange a left join uc_order b on a.orderNo = b.orderNo) c
left join uc_order_product d on c.orderNo = d.orderNo) e
left join (select o.id, p.imageUrl from uc_product o left join uc_goods p on o.goodsId = p.id) q on e.productId = q.id

    type = #{type}
    and status = #{status}
    and username = #{username}
    and orderNo = #{orderNo}
