mapper.select:
SELECT
o.id, o.orderNo, o.action, o.createTime, o.createUser, o.remark,u.nickname as createUserName,o.logType,o.exchangeId
FROM uc_order_log o
LEFT JOIN uc_user_admin u ON u.id = o.createUser

    1=1
    and o.orderNo = #{orderNo}
    AND o.logType = #{logType}
    AND o.exchangeId = #{exchangeId}

ORDER BY o.createTime desc
