mapper.select:
select uul.id,uul.userId,uul.ruleId,uur.name,uur.code,uur.type,uul.income,uul.outgo,uul.usablePoints,uul.createTime
from uc_upoints_log uul, uc_upoints_rule uur

    uul.ruleId = uur.id
    and name=#{name}
    and code=#{code}
    and type=#{type}
    and userId=#{userId}

ORDER BY createTime DESC

SELECT uul.id id, (SELECT uur.name FROM uc_upoints_rule uur WHERE uur.id = uul.ruleId) channel, (SELECT uur.points FROM uc_upoints_rule uur WHERE uur.id = uul.ruleId) points, (SELECT SUM(uu.income)-SUM(uu.outgo) sums FROM uc_upoints_log uu WHERE uu.userId=uul.userId) sums, uul.createTime createTime, (SELECT uur.description FROM uc_upoints_rule uur WHERE uur.id = uul.ruleId) remark FROM uc_upoints_log uul
and uul.userId=#{userId} and uul.createTime >= #{start} and uul.createTime < #{end}
ORDER BY uul.createTime DESC
