mapper.select:
select id, name, code, points, description, type, status, createTime, lastUpdate
from uc_upoints_rule

    name=#{name}
    and code=#{code}
    and type=#{type}

ORDER BY lastUpdate DESC

select id, name, code, points, description, type, status, createTime, lastUpdate
from uc_upoints_rule
where id=#{id}

select id, name, code, points, description, type, status, createTime, lastUpdate
from uc_upoints_rule
where code=#{code}
