mapper.select:
SELECT
id,name,startTime,endTime,rule,points,type,status,createTime,lastUpdate,imageUrl,ruleName,ruleCode,count,skipURL
from uc_sys_task


        name=#{name}


        and type=#{type}


ORDER BY lastUpdate DESC

SELECT id,name,startTime,endTime,rule,points,type,status,createTime,lastUpdate,imageUrl,ruleName,ruleCode,count,skipURL from uc_sys_task
status=1
and name=#{name}
and type=#{type}
ORDER BY lastUpdate DESC
