mapper.select:
SELECT id,tagName,status,createTime,lastUpdate,category,description,rule,(SELECT COUNT(1) FROM uc_subject_tag us
WHERE us.tagId=ut.id) tagedCount,weight
from uc_tag ut


        tagName=#{tagName}


        and category=#{category}


ORDER BY category ASC

SELECT  id,tagName,status,createTime,lastUpdate,category,description,rule,(SELECT COUNT(1) FROM uc_subject_tag us WHERE us.tagId=ut.id) tagedCount,weight
from uc_tag ut
where ut.id=#{id}
