SELECT

FROM uc_user_task_todo
WHERE userId = #{userId}
and type = #{type}
and createTime >= (SELECT CURDATE())
and createTime <(SELECT DATE_SUB(CURDATE(),INTERVAL -1 DAY))

SELECT

FROM uc_user_task_todo
WHERE userId = #{userId}
and sysTaskId = #{sysTaskId}
and createTime >= (SELECT CURDATE())
and createTime <(SELECT DATE_SUB(CURDATE(),INTERVAL -1 DAY))

