SELECT

FROM uc_user_task_todo
WHERE userId = #{userId}
and sysTaskId = #{sysTaskId}
and createTime >= (SELECT DATE_ADD(CURDATE(),INTERVAL -DAY(CURDATE())+1 DAY))
and createTime <(SELECT DATE_ADD(CURDATE() - DAY(CURDATE()) + 1, INTERVAL 1 MONTH))

