mapper.select:
SELECT id, username, phone, password, regMail, regIP, salt, nickname, status, createTime,
  lastUpdate, userPicturePath, maxUserPicturePath, midUserPicturePath, minUserPicturePath,
  points, exp, vipLevel, usernameModifiedTimes, vipExpireDate, wxopenid, wxheadimg, wxnickname, bdqudao, oldId
FROM uc_user


        id=#{id}


        wxopenid=#{wxopenid}

SELECT id, username, phone, nickname, status, createTime, lastUpdate, points, exp, vipLevel, wxnickname
FROM uc_user


        id=#{id}


        and username=#{username}


        and phone=#{phone}


        and nickname=#{nickname}


        and status=#{status}


        and vipLevel=#{vipLevel}


        and exp >= #{exp}


        and points >= #{points}


        and createTime >= #{createTime}

select uu.id, uu.username, uu.password, uu.phone, uu.regMail, uu.regIP, uu.salt, uu.nickname, uu.status, uu.createTime, uu.lastUpdate,uu.userPicturePath, uu.maxUserPicturePath, uu.midUserPicturePath, uu.minUserPicturePath, uu.points, uu.exp,uu.vipLevel,uu.usernameModifiedTimes,uu.vipExpireDate, uu.wxopenid,uu.wxheadimg,uu.wxnickname,uu.oldId, (select uul.level from uc_uvip_level uul where uul.levelCode = uu.vipLevel limit 1) vipLevelName, IFNULL( (SELECT uul.name FROM uc_uexp_level uul WHERE uul.status='1' AND uu.exp >= uul.minValue AND uu.exp <= uul.maxValue LIMIT 1), (SELECT uul.name FROM uc_uexp_level uul WHERE uul.status='1' AND uul.minValue >= (SELECT MAX(uull.minValue) FROM uc_uexp_level uull WHERE uull.status='1' LIMIT 1) AND uul.minValue <=uu.exp) ) level, IFNULL( (SELECT uul.medal FROM uc_uexp_level uul WHERE uul.status='1' AND uu.exp >= uul.minValue AND uu.exp <= uul.maxValue LIMIT 1), (SELECT uul.medal FROM uc_uexp_level uul WHERE uul.status='1' AND uul.minValue >=(SELECT MAX(uull.minValue) FROM uc_uexp_level uull WHERE uull.status='1' LIMIT 1) AND uul.minValue <=uu.exp) ) levelName from uc_user uu
uu.username = #{usernameOrPhone}
OR uu.phone = #{usernameOrPhone}

select id, userId, count, limitTime
from uc_login_password_wrong_count
where userId = #{userId}
