mapper.select:
select uu.id, uu.username, uu.phone, uu.regMail, uu.regIP, uu.salt, uu.nickname, uu.status, uu.createTime, uu.lastUpdate, uu.userPicturePath, uu.maxUserPicturePath, uu.midUserPicturePath, uu.minUserPicturePath, uu.points, uu.exp, uu.vipLevel,uu.usernameModifiedTimes,uu.vipExpireDate, (select uue.realName from uc_user_extend uue where uue.userId=uu.id) realName, (select uul.name from uc_uexp_level uul where uu.exp >= uul.minValue and uu.exp <= uul.maxValue) levelName, (select uul.medal from uc_uexp_level uul where uu.exp >= uul.minValue and uu.exp <= uul.maxValue) medal, (select uul.medalIcon from uc_uexp_level uul where uu.exp >= uul.minValue and uu.exp <= uul.maxValue) medalIcon from uc_user uu
username=#{username}
and phone=#{phone}
and nickname=#{nickname}
and status=#{status}
and uu.id in (SELECT a.subjectId FROM
SELECT subjectId FROM uc_subject_tag ust,uc_tag ut WHERE ust.tagId=ut.id AND ut.tagName=#{item}
a group by a.subjectId HAVING COUNT(subjectid) = #{tagNameCount})
ORDER BY uu.lastUpdate DESC

select uu.id, uu.username, uu.password, uu.phone, uu.regMail, uu.regIP, uu.salt, uu.nickname, uu.status, uu.createTime, uu.lastUpdate,uu.userPicturePath, uu.maxUserPicturePath, uu.midUserPicturePath, uu.minUserPicturePath, uu.points, uu.exp,uu.vipLevel,uu.usernameModifiedTimes,uu.vipExpireDate, uu.wxopenid,uu.wxheadimg,uu.wxnickname, (select ug.imageUrl from uc_goods ug where ug.memberLevel = uu.vipLevel limit 1) vipImage from uc_user uu
uu.username = #{usernameOrPhone}
OR uu.phone = #{usernameOrPhone}
