mapper.select:
select uu.id, uu.username, uu.phone, uu.password, uu.regMail, uu.regIP, uu.salt, uu.nickname, uu.status, uu.createTime, uu.lastUpdate, uu.userPicturePath, uu.maxUserPicturePath, uu.midUserPicturePath, uu.minUserPicturePath, uu.points, uu.exp, uu.vipLevel, (select uue.realName from uc_user_extend uue where uue.userId=uu.id) realName, (select uul.name from uc_uexp_level uul where uu.exp >= uul.minValue and uu.exp <= uul.maxValue) levelName, (select uul.medal from uc_uexp_level uul where uu.exp >= uul.minValue and uu.exp <= uul.maxValue) medal, (select uul.medalIcon from uc_uexp_level uul where uu.exp >= uul.minValue and uu.exp <= uul.maxValue) medalIcon from uc_user uu
left JOIN (SELECT ut.tagName,ut.status,ut.createTime,ut.lastUpdate,ut.category,ut.description,ut.rule,ust.userId,ust.subjectId,ust.tagId,ust.type FROM uc_subject_tag ust LEFT JOIN uc_tag ut ON ust.tagId=ut.id) utust on utust.subjectId=uu.id
username=#{username}
and phone=#{phone}
and nickname=#{nickname}
and status=#{status}
and utust.tagName=#{tagName}
ORDER BY uu.lastUpdate DESC

select uu.id, uu.username, uu.phone, uu.password, uu.regMail, uu.regIP, uu.salt, uu.nickname, uu.status, uu.createTime, uu.lastUpdate, uu.userPicturePath, uu.maxUserPicturePath, uu.midUserPicturePath, uu.minUserPicturePath, uu.points, uu.exp, uu.vipLevel, (select uue.realName from uc_user_extend uue where uue.userId=uu.id) realName, (select uul.name from uc_uexp_level uul where uu.exp >= uul.minValue and uu.exp <= uul.maxValue) levelName, (select uul.medal from uc_uexp_level uul where uu.exp >= uul.minValue and uu.exp <= uul.maxValue) medal, (select uul.medalIcon from uc_uexp_level uul where uu.exp >= uul.minValue and uu.exp <= uul.maxValue) medalIcon from uc_user uu
username=#{username}
and phone=#{phone}
and nickname=#{nickname}
and status=#{status}
and uu.id in (SELECT a.subjectId FROM
SELECT subjectId FROM uc_subject_tag ust,uc_tag ut WHERE ust.tagId=ut.id AND ut.tagName=#{item}
a group by a.subjectId HAVING COUNT(subjectid) = #{tagNameCount})
ORDER BY uu.lastUpdate DESC

select id, username, phone, password, regMail, regIP, salt, nickname, status, createTime, lastUpdate,
userPicturePath, maxUserPicturePath, midUserPicturePath, minUserPicturePath, points, exp, vipLevel
from uc_user


        username = #{usernameOrPhone}


        OR phone = #{usernameOrPhone}
