mapper.select:
select * from (select uu.id, uu.username, insert(uu.phone, 4, 4, '****') phone, uu.regMail, uu.regIP, uu.salt, uu.nickname, uu.status, uu.createTime, uu.lastUpdate, uu.userPicturePath, uu.maxUserPicturePath, uu.midUserPicturePath, uu.minUserPicturePath, uu.points, uu.exp, uu.vipLevel,uu.usernameModifiedTimes,uu.vipExpireDate, (select insert(uue.realName, 1, 1, '*') realName from uc_user_extend uue where uue.userId=uu.id) realName, (select uul.name from uc_uexp_level uul where uu.exp >= uul.minValue and uu.exp <= uul.maxValue) levelName, (select uul.medal from uc_uexp_level uul where uu.exp >= uul.minValue and uu.exp <= uul.maxValue) medal, (select uul.medalIcon from uc_uexp_level uul where uu.exp >= uul.minValue and uu.exp <= uul.maxValue) medalIcon from uc_user uu) u
username=#{username}
and phone=insert(#{phone}, 4, 4, '****')
and nickname=#{nickname}
and status=#{status}
and medal=#{medal}
and vipLevel=#{vipLevel}
and exp=#{exp}
and points=#{points}
and realName=insert(#{realName}, 1, 1, '*')
and u.id in (SELECT a.subjectId FROM
SELECT subjectId FROM uc_subject_tag ust,uc_tag ut WHERE ust.tagId=ut.id AND ut.tagName=#{item}
a group by a.subjectId HAVING COUNT(subjectid) = #{tagNameCount})
ORDER BY u.lastUpdate DESC

select uu.id, uu.username, uu.password, uu.phone, uu.regMail, uu.regIP, uu.salt, uu.nickname, uu.status, uu.createTime, uu.lastUpdate,uu.userPicturePath, uu.maxUserPicturePath, uu.midUserPicturePath, uu.minUserPicturePath, uu.points, uu.exp,uu.vipLevel,uu.usernameModifiedTimes,uu.vipExpireDate, uu.wxopenid,uu.wxheadimg,uu.wxnickname, (select ug.imageUrl from uc_goods ug where ug.memberLevel = uu.vipLevel limit 1) vipImage, (select uul.level from uc_uvip_level uul where uul.levelCode = uu.vipLevel limit 1) vipLevelName, IFNULL( (SELECT uul.name FROM uc_uexp_level uul WHERE uul.status='1' AND uu.exp >= uul.minValue AND uu.exp <= uul.maxValue LIMIT 1), (SELECT uul.name FROM uc_uexp_level uul WHERE uul.status='1' AND uul.minValue >= (SELECT MAX(uull.minValue) FROM uc_uexp_level uull WHERE uull.status='1' LIMIT 1) AND uul.minValue <=uu.exp) ) level, IFNULL( (SELECT uul.medal FROM uc_uexp_level uul WHERE uul.status='1' AND uu.exp >= uul.minValue AND uu.exp <= uul.maxValue LIMIT 1), (SELECT uul.medal FROM uc_uexp_level uul WHERE uul.status='1' AND uul.minValue >=(SELECT MAX(uull.minValue) FROM uc_uexp_level uull WHERE uull.status='1' LIMIT 1) AND uul.minValue <=uu.exp) ) levelName from uc_user uu
uu.username = #{usernameOrPhone}
OR uu.phone = #{usernameOrPhone}

select id, userId, count, limitTime
from uc_login_password_wrong_count
where userId = #{userId}
