mapper.select:
SELECT
(SELECT COUNT(1) allCount FROM uc_user WHERE vipLevel=#{levelCode}
) allCount,
(SELECT COUNT(1) increase FROM uc_uvip_log uulo WHERE uulo.levelId =#{levelCode} AND createTime >=#{start} AND createTime < #{end}
) increase,
(SELECT COUNT(1) lastIncrease FROM uc_uvip_log uulo WHERE uulo.levelId =#{levelCode} AND createTime >=#{lastStart} AND createTime <#{lastEnd}
) lastIncrease,
(SELECT COUNT(1) lastYearAll FROM uc_uvip_log uulo WHERE uulo.levelId =#{levelCode} AND createTime <#{start}
) lastYearAll
FROM DUAL
